<?php

namespace Vitoop\InfomgmtBundle\Repository;

use Vitoop\InfomgmtBundle\Entity\Conversation;
use Vitoop\InfomgmtBundle\Entity\RelConversationUser;
use Vitoop\InfomgmtBundle\Entity\User;


/**
 * RelConversationUserRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class RelConversationUserRepository extends ResourceRepository
{
    public function getEntityClass()
    {
        return RelConversationUser::class;
    }

    public function addUser(RelConversationUser $user)
    {
        $this->getEntityManager()->merge($user);
        $this->getEntityManager()->flush();
    }

    public function removeUser(RelConversationUser $user)
    {
        $this->getEntityManager()->remove($user);
        $this->getEntityManager()->flush();
    }

    public function getRel(User $user, Conversation $conversation)
    {
        return $this->createQueryBuilder('rcu')
            ->select('rcu')
            ->where('rcu.user = :user')
            ->andWhere('rcu.conversationData = :conversationData')
            ->setParameter('conversationData', $conversation->getConversationData())
            ->setParameter('user', $user)
            ->getQuery()
            ->getOneOrNullResult();
    }
}